,p_plug_template=>wwv_flow_imp.id(34412389186801495297)
,p_plug_display_sequence=>30
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with tables as (  ',
'    select table_name,',
'           replace(lower(table_name), ''_'', '' '') as table_name_display,',
'           nvl( num_rows, 0 )                   as num_rows,',
'           case --> used for coloring the nodes in the graph, align this to your needs',
'               when regexp_like(table_name, ''^(wwv_|apex_|apex\$)'', ''i'') then ''APEX''',
'               when regexp_like(table_name, ''^(all|user)_.*_mv$''  , ''i'') then ''Dictionary MViews''',
'               when regexp_like(table_name, ''^(eba|demo)_''        , ''i'') then ''Sample Apps''',
'               else ''uncategorized tables''',
'           end                                  as table_group',
'      from all_tables_mv',
'     where owner = :P1_OWNER',
'       and regexp_like (',
'               table_name,',
'               (select nvl(model.to_regexp_like(:P1_OBJECTS_INCLUDE), ''.*'') from dual),',
'               ''i'' )',
'       and not regexp_like (',
'               table_name,',
'               (select nvl(model.to_regexp_like(:P1_OBJECTS_EXCLUDE), chr(10)) from dual),',
'               ''i'' )',
'       and table_name in (',
'               select distinct table_name ',
'                 from all_tab_columns_mv ',
'                where owner = :P1_OWNER',
'                  and regexp_like (',
'                          column_name,',
'                          (select nvl(model.to_regexp_like(:P1_COLUMNS_INCLUDE), ''.*'') from dual),',
'                          ''i'') ) ),',
'relations as (',
'    select distinct',
'           table_name,',
'           r_table_name,',
'           nullable_cons',
'      from all_relations_mv',
'     where owner = :P1_OWNER',
'       and ( table_name   in (select table_name from tables)',
'             or',
'             r_table_name in (select table_name from tables) ',
'           ) )',
'select json_object ( ''data'' value ',
'           json_object ( ',
'               ''nodes'' value ( ',
'                       select json_arrayagg (',
'                                  json_object (',
'                                      ''ID''         value table_name,',
'                                      ''LABEL''      value table_name_display,',
'                                      ''COLORVALUE'' value table_group,',
'                                      ''SIZEVALUE''  value num_rows ) ',
'                              returning clob )',
'                         from tables ),',
'               ''links'' value ( ',
'                       select json_arrayagg (',
'                                  json_object (',
'                                      ''FROMID'' value table_name,',
'                                      ''TOID''   value r_table_name,',
'                                      ''STYLE''  value case nullable_cons',
'                                                     when ''Y'' then ''dashed''',
'                                                     when ''N'' then ''solid''',
'                                                     end )',
'                              returning clob )',
'                         from relations )',
'           returning clob )',
'       returning clob ) as single_clob_result',
'  from dual'))
,p_plug_source_type=>'PLUGIN_NET.GOBRECHTS.D3.FORCE'
,p_ajax_items_to_submit=>'P1_OWNER,P1_OBJECTS_INCLUDE,P1_OBJECTS_EXCLUDE,P1_COLUMNS_INCLUDE'
