'with not_nulls as (',
'    select c.table_name as constraint_table,',
'           max(cc.position) as primary_key,',
'           cc.column_name as constraint_column,',
'           max(c.constraint_type) as constraint_type',
'      from all_constraints_mv c',
'      join all_cons_columns_mv cc',
'        on c.owner           = cc.owner',
'       and c.constraint_name = cc.constraint_name',
'     where c.owner = :P1_OWNER',
'       and c.status = ''ENABLED''',
'       and ( ',
'             c.constraint_type = ''P''',
'             or',
'             c.constraint_type = ''C'' and regexp_count (c.search_condition_vc, ''^\s*"?'' || cc.column_name || ''"?\s+is\s+not\s+null\s*$'', 1, ''i'' ) = 1',
'           )',
'     group by c.table_name, cc.column_name ',
')',
'select -- additional custom columns',
'       upper(substr(data_type,1,1)) || lower(substr(data_type,2)) || ',
'       case',
'         when data_type in (''CHAR'', ''NCHAR'', ''VARCHAR2'', ''NVARCHAR2'') then',
'           '' ('' || char_length || case char_used when ''B'' then '' byte'' when ''C'' then '' char'' end || '')''',
'         when data_type = ''NUMBER'' and data_precision is not null and data_scale is not null then',
'           '' ('' || data_precision || '','' || data_scale || '')''',
'       end as data_type_display,',
'       --',
'       case',
'         when nullable = ''N'' or constraint_column is not null',
'         then ''No''',
'         else ''Yes''',
'       end as nullable_constraints,',
'       --',
'       primary_key,',
'       --',
'       case when identity_column = ''YES'' then lower(identity_generation_type) || '' '' end ||',
'       case when default_on_null = ''YES'' then ''on null'' end',
'       as default_type,',
'       -- all columns of materialized dictionary view',
'       tc.*',
'  from all_tab_columns_mv tc',
'  left join not_nulls',
'    on table_name  = constraint_table',
'   and column_name = constraint_column',
' where owner = :P1_OWNER',
'   and table_name in ( select table_name from all_tables_mv',
'                        where owner = :P1_OWNER )',
'   and regexp_like (',
'           table_name,',
'           (select nvl(model.to_regexp_like(:P1_OBJECTS_INCLUDE), ''.*'') from dual),',
'           ''i'' )',
'   and not regexp_like (',
'           table_name,',
'           (select nvl(model.to_regexp_like(:P1_OBJECTS_EXCLUDE), chr(10)) from dual),',
'           ''i'' )',
'   and regexp_like (',
'           column_name,',
'           (select nvl(model.to_regexp_like(:P1_COLUMNS_INCLUDE), ''.*'') from dual),',
'           ''i'')'))
