,p_parent_plug_id=>wwv_flow_imp.id(1620390594009956)
,p_region_template_options=>'#DEFAULT#:t-Region--noPadding:t-Region--hideHeader:t-Region--noBorder:t-Region--scrollBody:margin-top-none:margin-bottom-none'
,p_plug_template=>wwv_flow_imp.id(34412389186801495297)
,p_plug_display_sequence=>50
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with ',
'direct_relations as (',
'    select r_owner || r_table_name as owner_table_name',
'      from all_relations_mv',
'     where owner = :P1_OWNER and table_name = :P1_OBJECT_NAME',
'     union',
'    select owner || table_name',
'      from all_relations_mv',
'     where r_owner = :P1_OWNER and r_table_name = :P1_OBJECT_NAME ),',
'relations as (',
'    select table_name,',
'           r_table_name,',
'           nullable_cons',
'      from all_relations_mv',
'     where owner || table_name     in (select owner_table_name from direct_relations)',
'        or r_owner || r_table_name in (select owner_table_name from direct_relations) ),',
'tables as (  ',
'    select table_name,',
'           replace(lower(table_name), ''_'', '' '') ||',
'           case when owner != :P1_OWNER then ',
'               '' ('' || replace(lower(owner), ''_'', '' '') || '')''',
'           end                                  as table_name_display,',
'           nvl( num_rows, 0 )                   as num_rows,',
'           case table_name --> used for coloring the nodes in the graph, align this to your needs',
'               when :P1_OBJECT_NAME then ''#ff7f0e''',
'               else ''#1f77b4''',
'           end                                  as color_value,',
'           case table_name --> used for labeling the nodes in the graph, align this to your needs',
'               when :P1_OBJECT_NAME then ''Selected table''',
'               else ''Connected Tables''',
'           end                                  as color_label',
'      from all_tables_mv',
'     where ( owner = :P1_OWNER and table_name = :P1_OBJECT_NAME',
'             or ',
'             owner || table_name in (select owner_table_name from direct_relations) )',
'       and not regexp_like (',
'               table_name,',
'               (select nvl(model.to_regexp_like(:P1_MODEL_EXCLUDE_TABLES), chr(10)) from dual),',
'               ''i'' ) )',
'select json_object ( ''data'' value ',
'           json_object ( ',
'               ''nodes'' value ( ',
'                       select json_arrayagg (',
'                                  json_object (',
'                                      ''ID''         value table_name,',
'                                      ''LABEL''      value table_name_display,',
'                                      ''COLORVALUE'' value color_value,',
'                                      ''COLORLABEL'' value color_label,',
'                                      ''SIZEVALUE''  value num_rows ) ',
'                              returning clob )',
'                         from tables ),',
'               ''links'' value ( ',
'                       select json_arrayagg (',
'                                  json_object (',
'                                      ''FROMID'' value table_name,',
'                                      ''TOID''   value r_table_name,',
'                                      ''STYLE''  value case nullable_cons',
'                                                     when ''Y'' then ''dashed''',
'                                                     when ''N'' then ''solid''',
'                                                     end )',
'                              returning clob )',
'                         from relations )',
'           returning clob )',
'       returning clob ) as single_clob_result',
'  from dual'))
,p_plug_source_type=>'PLUGIN_NET.GOBRECHTS.D3.FORCE'
,p_ajax_items_to_submit=>'P1_OWNER,P1_OBJECTS_INCLUDE,P1_OBJECTS_EXCLUDE,P1_COLUMNS_INCLUDE'
