'       upper(substr(data_type,1,1)) || lower(substr(data_type,2)) || ',
'       case',
'         when data_type in (''CHAR'', ''NCHAR'', ''VARCHAR2'', ''NVARCHAR2'') then',
'           '' ('' || char_length || case char_used when ''B'' then '' byte'' when ''C'' then '' char'' end || '')''',
'         when data_type = ''NUMBER'' and data_precision is not null and data_scale is not null then',
'           '' ('' || data_precision || '','' || data_scale || '')''',
'       end as data_type_display,',
'       --',
'       case',
'         when nullable = ''N'' or constraint_column is not null',
'         then ''No''',
'         else ''Yes''',
'       end as nullable_constraints,',
'       --',
'       primary_key,',
'       --',
'       case when identity_column = ''YES'' then lower(identity_generation_type) || '' '' end ||',
'       case when default_on_null = ''YES'' then ''on null'' end',
'       as default_type,',
'       -- all columns of materialized dictionary view',
'       tc.*',
'  from all_tab_columns_mv tc',
'  left join not_nulls',
'    on column_name = constraint_column',
' where owner      = :P1_OWNER',
'   and table_name = :P1_OBJECT_NAME'))
