'',
'--------------------------------------------------------------------------------',
'',
'function get_table_query (',
'    p_table_name  in varchar2,',
'    p_owner       in varchar2 default sys_context(''USERENV'', ''CURRENT_USER'') )',
'    return varchar2',
'is',
'    l_return varchar2( 32767 ) := ''select '';',
