'    end loop;',
'',
'    return rtrim( l_return, '', '' ) || '' from '' || p_table_name;',
'end get_table_query;',
'',
'--------------------------------------------------------------------------------',
'',
'function get_table_headers (',
'    p_table_name  in varchar2,',
'    p_owner       in varchar2 default sys_context(''USERENV'', ''CURRENT_USER''),',
'    p_delimiter   in varchar2 default '':'',',
