'        select',
'            item_name',
'        from',
'            apex_application_items',
'        where',
'            application_id = p_app_id )',
'    loop',
'        l_app_items ( i.item_name ) := null; -- we need only the key',
'    end loop;',
'',
'    -- create app items as needed',
'    create_items( c_N     );',
'    create_items( c_D     );',
'    create_items( c_TSLTZ );',
'    create_items( c_TSTZ  );',
'    create_items( c_TS    );',
'    create_items( c_VC    );',
'    create_items( c_CLOB  );',
'',
'end create_application_items;',
