'function get_overview_counts (',
'    p_owner           in varchar2 default sys_context(''USERENV'', ''CURRENT_USER'') ,',
'    p_objects_include in varchar2 default null ,',
'    p_objects_exclude in varchar2 default null ,',
'    p_columns_include in varchar2 default null )',
'    return varchar2',
'is',
'    l_return varchar2(4000);',
'begin',
'    select json_object (',
'           ''TABLES'' value',
'             ( select count(*)',
'                 from all_tables_mv t',
'                where owner = p_owner',
'                  and regexp_like (',
'                          table_name,',
'                          (select nvl(model.to_regexp_like(p_objects_include), ''.*'') from sys.dual),',
'                          ''i'' )',
'                  and not regexp_like (',
'                          table_name,',
'                          (select nvl(model.to_regexp_like(p_objects_exclude), chr(10)) from sys.dual),',
'                          ''i'' )',
'                  and table_name in (',
'                          select distinct table_name',
'                            from all_tab_columns_mv',
'                           where owner = p_owner',
'                             and regexp_like (',
'                                     column_name,',
'                                     (select nvl(model.to_regexp_like(p_columns_include), ''.*'') from sys.dual),',
'                                     ''i'') ) ),',
'           ''TABLE_COLUMNS'' value',
'              ( select count(*)',
'                  from all_tab_columns_mv',
'                 where owner = p_owner',
'                   and table_name in ( select table_name from all_tables_mv',
'                                        where owner = p_owner )',
'                   and regexp_like (',
'                           table_name,',
'                           (select nvl(model.to_regexp_like(p_objects_include), ''.*'') from sys.dual),',
'                           ''i'' )',
'                   and not regexp_like (',
'                           table_name,',
'                           (select nvl(model.to_regexp_like(p_objects_exclude), chr(10)) from sys.dual),',
'                           ''i'' )',
'                   and regexp_like (',
'                           column_name,',
'                           (select nvl(model.to_regexp_like(p_columns_include), ''.*'') from sys.dual),',
'                           ''i'') ),',
'           ''INDEXES'' value',
'              ( select count(*)',
'                  from all_indexes_mv',
'                 where owner = p_owner',
'                   and regexp_like (',
'                           table_name,',
'                           (select nvl(model.to_regexp_like(p_objects_include), ''.*'') from sys.dual),',
'                           ''i'' )',
'                   and not regexp_like (',
'                           table_name,',
'                           (select nvl(model.to_regexp_like(p_objects_exclude), chr(10)) from sys.dual),',
'                           ''i'' )',
'                   and index_name in (',
'                           select index_name',
'                             from all_ind_columns_mv',
'                            where regexp_like (',
'                                      column_name,',
'                                      (select nvl(model.to_regexp_like(p_columns_include), ''.*'') from sys.dual),',
'                                      ''i'') ) ),',
'           ''VIEWS'' value',
'              ( select count(*)',
'                  from all_views_mv t',
'                 where owner = p_owner',
'                   and regexp_like (',
'                           view_name,',
'                           (select nvl(model.to_regexp_like(p_objects_include), ''.*'') from sys.dual),',
'                           ''i'' )',
'                   and not regexp_like (',
'                           view_name,',
'                           (select nvl(model.to_regexp_like(p_objects_exclude), chr(10)) from sys.dual),',
'                           ''i'' )',
'                   and view_name in (',
'                           select distinct table_name',
'                             from all_tab_columns_mv',
'                            where owner = p_owner',
'                              and regexp_like (',
'                                      column_name,',
'                                      (select nvl(model.to_regexp_like(p_columns_include), ''.*'') from sys.dual),',
'                                      ''i'') ) ),',
'           ''VIEW_COLUMNS'' value',
'              ( select count(*)',
'                  from all_tab_columns_mv',
'                 where owner = p_owner',
'                   and table_name in ( select view_name from all_views_mv',
'                                        where owner = p_owner )',
'                   and regexp_like (',
'                           table_name,',
'                           (select nvl(model.to_regexp_like(p_objects_include), ''.*'') from sys.dual),',
'                           ''i'' )',
'                   and not regexp_like (',
'                           table_name,',
'                           (select nvl(model.to_regexp_like(p_objects_exclude), chr(10)) from sys.dual),',
'                           ''i'' )',
'                   and regexp_like (',
'                           column_name,',
'                           (select nvl(model.to_regexp_like(p_columns_include), ''.*'') from sys.dual),',
'                           ''i'') ),',
'           ''M_VIEWS'' value',
'              ( select count(*)',
'                  from sys.all_mviews t',
'                 where owner = p_owner',
'                   and regexp_like (',
'                           mview_name,',
'                           (select nvl(model.to_regexp_like(p_objects_include), ''.*'') from sys.dual),',
'                           ''i'' )',
'                   and not regexp_like (',
'                           mview_name,',
'                           (select nvl(model.to_regexp_like(p_objects_exclude), chr(10)) from sys.dual),',
'                           ''i'' ) ),',
'           ''OTHER_OBJECTS'' value',
'              ( select count(*)',
'                  from all_objects_mv',
'                 where owner = p_owner',
'                   and object_type not in (''TABLE'', ''INDEX'', ''VIEW'', ''MATERIALIZED VIEW'')',
'                   and regexp_like (',
'                           object_name,',
'                           (select nvl(model.to_regexp_like(p_objects_include), ''.*'') from sys.dual),',
'                           ''i'' )',
'                   and not regexp_like (',
'                           object_name,',
'                           (select nvl(model.to_regexp_like(p_objects_exclude), chr(10)) from sys.dual),',
'                           ''i'' ) )',
'      )',
'      into l_return',
'      from sys.dual;',
'',
'    return l_return;',
'end get_overview_counts;',
'',
'--------------------------------------------------------------------------------',
